REPLACE INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES
(12966, 'ruRU', 'Добро пожаловать в Гранитные ключи, друг.$b$bЕсли у тебя получится поймать одного из этих ледяных троллей живым, обязательно дай мне знать. Мы за них хорошо платим.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(12968, 'ruRU', 'Эй, гляжу, у тебя есть несколько жетонов Хмельного фестиваля. Может быть, ты хочешь их обменять на что-нибудь?', 'Эй, гляжу, у тебя есть несколько жетонов Хмельного фестиваля. Может быть, ты хочешь их обменять на что-нибудь?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12970, 'ruRU', 'Как дела, дружище $n? $gПришел:Пришла; пообщаться с другими членами клуба?', 'Как дела, дружище $n? $gПришел:Пришла; пообщаться с другими членами клуба?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12971, 'ruRU', 'Рад снова видеть тебя, $N. Надеюсь, тебе нравится быть членом нашего клуба.', 'Рада снова видеть тебя, $N. Надеюсь, тебе нравится быть членом нашего клуба.', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(12972, 'ruRU', 'Эй, $n, как дела? $gПришел:Пришла; пообщаться с другими членами клуба?', 'Эй, $n, как дела? $gПришел:Пришла; пообщаться с другими членами клуба?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12976, 'ruRU', '', '$N, как я рада тебя видеть!$B$BИли же это наша первая встреча?$B$BПрямо сейчас я нахожусь в столь многих местах и временах разом, что совершенно не могу за всем этим уследить!', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(12977, 'ruRU', 'Гниль, гниль, гниль… это все, о чем здесь говорят. Мне не хватает разнообразия!$b$bТак-так. У вас есть домашние любимцы, да? Конечно, есть. Конечно. Маленькие животные. У меня есть микстура для них. Она сделает их умными и сильными существами, да? Да. Сильными.$b$bПопробуйте ее!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(12978, 'ruRU', 'Осторожно с ним. Оно нестабильно и быстро теряет силу. Используй его поскорее! И… лучше сделай это в своем поле зрения, да…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(12982, 'ruRU', 'Как-то раз я ушел с войны, чтобы разобраться с неприятностями дома на Западном краю. Кажется, что это было целую жизнь назад. Благодарение Свету, что Братство Справедливости более не представляет угрозы.$B$BМы здесь далеко от дома, но если мы проиграем эту битву, у нас может не оказаться Западного края, куда мы могли бы вернуться.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(12990, 'ruRU', 'Мои военные маги направляются сюда и скоро прибудут.$b$bСкажи мне, когда они вернутся.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12991, 'ruRU', 'Прошу прощения, |3-6($r), но я здесь по официальному делу и меня нельзя беспокоить.$b$bПрошу простить меня.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12992, 'ruRU', '', 'Превосходно! Теперь Артас знает о зараженном чумой зерне в Стратхольме, и он вот-вот прикажет начать его зачистку.$b$bНо что-то все еще не так. Я чувствую присутствие чего-то чуждого во временном пространстве, и это должно означать, что драконы Бесконечности где-то рядом.', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(12993, 'ruRU', '', 'Я не знаю, и это меня беспокоит. Я сделаю что смогу, чтобы найти драконов Бесконечности.$b$bИ сейчас все, что мне от тебя нужно, – это чтобы ты $gдержался:держалась; как можно ближе к Артасу, вступив в ряды его армии.', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(12994, 'ruRU', NULL, 'Что ж, тебе не нужно записываться в наемники, просто сражаться рядом с ним. Главное – убедиться в том, что Артас вычистит Стратхольм и победит Мал\'Ганиса. Теперь, когда рядом нет Утера и Джайны, ему понадобится любая помощь. Если ты поговоришь с Артасом, он отправит тебя уничтожать войска Мал\'Ганиса. Выполняй этот приказ до тех пор, пока я не выясню, что происходит с драконами Бесконечности. Я свяжусь с тобой, когда у меня появится новая информация.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12995, 'ruRU', NULL, 'Удачи тебе и будь $gосторожен:осторожна;. Теперь сюда идут Утер и Джайна.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12998, 'ruRU', '<Дракуру протягивает свою истекающую кровью руку через прутья клетки и выжидательно смотрит на тебя.>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12999, 'ruRU', 'Были времена, когда я вырвал бы тебе сердце просто для того, чтобы посмотреть, какое у тебя станет лицо…', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13001, 'ruRU', 'Привет, $gбратишка:сестренка;!$b$bРад снова тебя видеть…$N, да? Да, да, конечно.$b$bМы с тобой будем добрыми друзьями. Дракуру в таких вещах разбирается.$b$b<Раскрашенное лицо тролля медленно расплывается в улыбке.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13002, 'ruRU', 'Вот, держи. Постарайся хотя бы в этот раз не потерять, ладно?$b$bА теперь отправляйся в руины Драк\'Зина и выпей это у пьедестала!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13003, 'ruRU', 'Именно с этого трона я отдаю команды Адского Крика. Его слово – жизнь и его слово – смерть.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13004, 'ruRU', '', 'Да благословит тебя Элуна, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13005, 'ruRU', 'Чем я могу помочь вам, $gсэр:мэм;?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13006, 'ruRU', 'Мой партнер убивает всяких существ, а я вытягиваю часть их сущности… самую капельку. Мелочи, право.$B$BНо, чтобы до конца выполнить свою часть контракта, я вынужден платить эфириальными монетами.$B$BЗа эти монеты можно купить у меня некоторые вещи. Я вынужден иметь при себе то, что может тебя заинтересовать…', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13009, 'ruRU', '', 'Только так Гарри может заговорить со мной! И мне приходится все время носить эту дурацкую маску!', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13011, 'ruRU', 'Привет, $gбратишка:сестренка;!$b$bЭто не совсем то, чем кажется. Ну, может быть…$b$bВсе очень сложно.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13012, 'ruRU', 'Эй, ты! Я тебя знаю?$b$bАх, да, у Пригмона хорошая память на лица.$b$bА ты не следишь ли за мной случайно? Что бы там ни было, Пригмон этого не делал!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13018, 'ruRU', 'Приветствую, |3-6($r).$B$BЕсли ты $gхотел:хотела; бы помочь в обороне храма, пожалуйста, поговори с лордом Афрасаcтразом.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13019, 'ruRU', 'Добро пожаловать в мой лагерь, |3-6($r).$b$bОн выглядит не ахти, но сейчас это наш дом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13022, 'ruRU', 'Чем я могу помочь тебе, дитя?', 'Чем я могу помочь тебе, дитя?', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13023, 'ruRU', NULL, 'Мы с Мистером Ушастиком отправились на прогулку, но заблудились, и я испугалась, потому что, понимаешь, там были волки, и они погнались за нами. Но потом я увидела эту башню, и мы с Мистером Ушастиком спрятались тут. Волки ушли, но я все еще боялась и не знала, как нам добраться домой…', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13024, 'ruRU', '', 'Думаю, тебе лучше уйти. Ты пугаешь мистера Ушастика.', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13025, 'ruRU', 'Добро пожаловать в храм Драконьего Покоя, |3-6($r). Прости нас за шумиху внизу, из-за последних действий Малигоса мы оказались под осадой. Ты можешь тем не менее отдохнуть здесь под нашей защитой, если она тебе понадобится.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13030, 'ruRU', 'Приветствую, |3-6($c)!$b$bНадеюсь, ты не против немного поработать. Мы были бы очень рады, если бы ты нам $gпомог:помогла;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13031, 'ruRU', '', 'Ветер и деревья шепчут о беде – о той, что крадется незримо… Кажется, что эта земля здорова; но здесь происходит что-то, чего мы не видим…', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13032, 'ruRU', NULL, '<Тайрин смотрит перед собой невидящими глазами. Судя по всему, очищение Фодрассила от скверны отняло у друида слишком много сил.>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13033, 'ruRU', 'Шкуры для Орды, $gбратишка:сестренка;! Все самое лучшее для наших воинов!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13034, 'ruRU', 'Запасы в дорогу? Может быть, освежающий напиток, пока вы смотрите бои?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13035, 'ruRU', 'Нас окружили со всех сторон, и старейшины собираются бежать, оставив эти земли врагу. Мне кажется, это неправильно! Мы должны остаться и сражаться за нашу родину!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13042, 'ruRU', 'Я зову ее Духом Гномрегана. Ну не красотка ли?! Без твоей помощи ее никогда бы не удалось восстановить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13043, 'ruRU', 'Добро пожаловать на Серебряную заставу, |3-6($c).$b$bРасполагайся как дома, но не слишком расслабляйся. Тут еще полно опасностей.$b$bЕсли мы хотим выбраться отсюда целыми и невредимыми, всем нужно будет поработать… в том числе тебе.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13044, 'ruRU', 'Жуткий капитан ДеМеза захватила Пиратскую бухту и объявила празднование Дня пирата!$B$BОна приглашает всех смельчаков выпить вместе с ней и ее командой.', 'Жуткий капитан ДеМеза захватила Пиратскую бухту и объявила празднование Дня пирата!$B$BОна приглашает всех смельчаков выпить вместе с ней и ее командой.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13045, 'ruRU', 'Ну и в заварушку мы попали.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13046, 'ruRU', 'Эти земли осквернены присутствием Плети с одной стороны и ритуалами Драккари – с другой.$B$BМы твердо держим наши позиции между ними.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13047, 'ruRU', 'Да хранит тебя Свет!', 'Да хранит тебя Свет!', 'Эй, ты там, |3-6($c)! У нас тут бой в разгаре.', 'Эй, ты там, |3-6($c)! У нас тут бой в разгаре.', 'Что ты там делаешь?', 'Что ты там делаешь?', 'А ты не $N? Я слышал, ты $gпогиб:погибла;.', 'А ты не $N? Я слышала, ты $gпогиб:погибла;.', 'Только быстрее… сражаться здесь не очень-то приятно. Мне нужно быть начеку.', 'Только быстрее… сражаться здесь не очень-то приятно. Мне нужно быть начеку.', 'Ты – новый рекрут? Это не предписанная уставом форма.', 'Ты – новый рекрут? Это не предписанная уставом форма.', 'Отлично, подкрепление подоспело!', 'Отлично, подкрепление подоспело!', 'У тебя для меня сообщение?', 'У тебя для меня сообщение?'),
(13048, 'ruRU', 'Здесь немножко опаснее, чем ты $gожидал:ожидала;, |3-6($r)?$b$bНе могу сказать, что я удивлен. Я это слышу почти ото всех, кто добирается досюда.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13050, 'ruRU', 'Я Убунго. Я представляю племя Зандалар.$B$BПлохие новости, |3-6($r). Драккари сами станут причиной своей смерти, учитывая, чем они заняты.$B$BНекоторые тролли никогда ничему не учатся.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13055, 'ruRU', 'Хей, дорогуша!$B$BСегодня НАШ день… ДЕНЬ ПИРАТА!$B$BМы тут придумали кое-что со стариной Манком, и как только Жуткий Капитан услышала про нашу идею, тут же послала нас сюда!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13056, 'ruRU', 'Завтра барон снова вернет свой город… но сегодня, в День пирата, он принадлежит Жуткому Капитану де Мезе и ее команде! Арг!$B$BИ помни – мало напялить на себя пиратские шмотки. Говори, как пират, $gдружище:подруга;!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13057, 'ruRU', 'Апана скалится и машет вам рукой, указывая на Жуткого Капитана де Мезу.$B$BПод густыми волосами, покрывающими его мускулистую руку, можно различить татуировку со словом \"Магата\" в пронзенном стрелой сердце\".', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13058, 'ruRU', 'Йо-хо-хо! Якорь тебе в глотку! Якорь!', NULL, 'Кальмар-ррьи кишки! Кальмар-ррьи!', NULL, 'ПИАСТРЫ! Свистать всех наверх! Пиастры!', NULL, 'Пиастры! Шевелитесь, сухопутные крысы!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13059, 'ruRU', NULL, 'Сегодня День пирата, знаешь ли… и до восхода солнца Пиратская Бухта МОЯ!$B$BНу, что скажешь? Выпей за меня и мою команду и присоединяйся к нашему празднику!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13060, 'ruRU', 'Ой… что-то у меня голова закружилась…', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13061, 'ruRU', 'Не приставай ко мне, |3-6($c)! У меня тут горячий цех – надо следить за тем, чтобы эти два котла с пивом не выкипели!$B$BЕсли вкус будет не слишком хорош, мастер Корен Худовар сварит меня там живьем!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
(13062, 'ruRU', 'Добро пожаловать в Пиратскую Бухту, партнер. Не обращай внимания на де Мезу и ее команду… раз в год мы разрешаем ей провести День пирата, после чего эта бунтарша вновь исчезает в глубинах Южных морей.$B$BНу а теперь… чем скромный слуга картеля \"Хитрая Шестеренка\" может быть тебе полезен? Или лучше – чем ты можешь быть $gполезен:полезна; мне?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13063, 'ruRU', 'Эх, хорошо пошла! Аж согревает изнутри! Кстати, дружище $N, я тебе говорил, что мне нравится твой стиль одежды?', NULL, 'О, да, $gсэр:мэм;, это отличный напиток! Можно сказать, превосходный!', NULL, 'Давай споем!$B$BЛя-ля,$B$Bля-ля!$B$BЧерное железо – это для меня!$B$BХа-ха. Нравишься ты мне, $N!', NULL, 'На чем это я остановился? Ах, да! Я сказал ей: \"Ты нужна мне! Это все, что мне отпущено знать!\"… а она рассмеялась и ускользнула прочь! Ах, $N, эти наги могут быть такими жестокими!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13064, 'ruRU', 'Отличное пиво! Правда, слегка слабовато, на мой вкус. Я бы добавил немного чесночного душка…', NULL, 'Хорошо получилось! Но мне кажется, оно немного плоское. Давай-ка закруглим его с помощью нескольких мягких глаз василиска…', NULL, 'Неплохо, неплохо… Правда, не хватает перчинки! Может быть, добавить яда скорпида? Точно! Получится то, что надо!', NULL, 'Ага, уже лучше! Но все равно, чего-то не хватает… О, точно! Давай постираем в котле носки недельной давности… Ммм, вкуснятина!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13065, 'ruRU', NULL, 'Йо-хо! Сегодня День пирата, знаешь ли… и до восхода солнца Пиратская Бухта МОЯ!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(13068, 'ruRU', 'Истинно, ужасы, которые происходят здесь, превосходят даже те, что мы испытали в Чумных землях.$b$bТеперь силы Плети более могущественны, чем когда-либо.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''),
